                              {/* Job Name */}
                              <Col md="6" className="form-group">
                                 <label htmlFor="status">Job Name</label>
                                 <InputGroup seamless>
                                 <InputGroupAddon type="prepend">
                                 </InputGroupAddon>
                                 <FormInput
                                    id="jobName"
                                    onChange={() => {}}
                                 />
                                 </InputGroup>
                              </Col>

